import React,{ useState } from "react"

function task(){
    const [names,setNames]=useState([])
    const [newName,setnewName]=useState("")
    const [editIndex,setEditIndex]=useState(-1)

    const handleInputChange=(event)=>{
        setnewName(event.target.value);
    };
    const handleSubmit=(event)=>{
        event.preventDefault();
        if(editindex > -1){
            let temp=[...names]
            temp[editindex]=newName
            setNames(temp)
        }else{
            setNames ([...names,newName])
        }
        setNames("");
        setEditIndex(-1)

        const handleEdit=(index)=>{
            setEditIndex(index);
            setnewName(names[index])

        }
        return(
            <div className="task-container">
            <h1>Names Table</h1>
            <form onSubmit={handleSubmit}>
            <label>Name:<input type="text" value={newName} onChange={handleInputChange}/></label>
            {editindex > -1 ? (<button className="task-save-button" type="submit">save</button>):(<button className="task-add-button" type="submit" >Add Name</button>)}
            </form>
            <table className="task-table">
            <thead>
            <tr>
            <th>Name</th>
            <th>Edit</th>
            </tr>
            </thead>
            <tbody>
            {names.map((name,index)=>(
                <tr key={index}>
                <td>{editIndex===index ? 
                <input type="text" value={newName} onChange={handleInputChange}/>: name}</td>
                <td>
                {editIndex === index ?(<button className="task-save-button" type="submit">Save</button>): (<button className="task-edit-button" type="button" onClick={()=>handleEdit(index)}>edit</button>)} 


                </td>

                </tr>
                ))}
                </tbody>
            </table>

            </div>
        )
    }

}
export default task;
