import {useState, createContext ,useContext} from "react"

const MyContext=createContext()

function ContextApi(){

    const[number,setNumber]=useState(0)

    const incrementNumber=()=>{
        setNumber(number+1)
    }

    return(
        <MyContext.Provider value={{number,setNumber}}>
        <div>
            <h1>Context API Demo!!!</h1>
            <p>Number in Parent : {number}</p>
            <button type="button" onClick={incrementNumber}>Add</button>
            <TestContextOne/>
            <TestContextTwo/>
        </div>
        </MyContext.Provider>
    )
}

function TestContextOne(){

    const {number,setNumber}=useContext(MyContext)

    const incrementValue=()=>{
        setNumber
    }

    return(
        <div>
            <h2>Context One Component!!!</h2>
            <p>Number in ContextOne : {number} </p>
            <button type="button" onClick={incrementValue}>Add</button>
        </div>
    )
}

function TestContextTwo(){

    const number=useContext(MyContext)

    return(
        <div>
            <h2>Context Two Component!!!</h2>
            <p>Number in ContextTwo : {number} </p>
        </div>
    )
}

export default ContextApi
